h1 {
  color: red;          /* Named color */
  color: #ff0000;      /* Hex */
  color: rgb(255, 0, 0); /* RGB */
}

div {
  width: 100px;
  height: 100px;
  padding: 10px;
  border: 2px solid black;
  margin: 20px;
}

p {
  font-size: 16px;
  font-family: Arial, sans-serif;
  color: darkblue;
  text-align: center;
  text-decoration: underline;
}

body {
  background-color: lightgrey;
  background-image: url("bg.jpg");
  background-repeat: no-repeat;
  background-size: cover;
}

div {
  border: 2px dashed red;
  border-radius: 10px;
}

.box {
  padding: 20px;
  margin: 15px;
}

.inline {
  display: inline;
}

.block {
  display: block;
}

.none {
  display: none;
}

.absolute-box {
  position: absolute;
  top: 50px;
  left: 100px;
}

.relative-box {
  position: relative;
  top: 10px;
  left: 10px;
}

.container {
  display: flex;
  justify-content: center;
  align-items: center;
}

.grid-container {
  display: grid;
  grid-template-columns: auto auto auto;
  gap: 10px;
}

a:hover {
  color: orange;
  text-decoration: underline;
}

@media (max-width: 600px) {
  body {
    background-color: lightblue;
  }
}

div {
  transition: background-color 0.5s ease;
}

div:hover {
  background-color: pink;
}

@keyframes slideIn {
  from {
    transform: translateX(-100%);
  }
  to {
    transform: translateX(0);
  }
}

.box {
  animation: slideIn 1s ease-in-out;
}

.box1 {
  position: absolute;
  z-index: 2;
}

.box2 {
  position: absolute;
  z-index: 1;
}

